import logo from './logo.svg';
import './App.css';
import React, {useState, useEffect} from 'react'

function App() {
  const [dato,setDato] = useState(0);
  const [respuesta, SetRespuesta] = useState(0);

  const onzas = () => {

    return (dato)/0.0625
  }

  useEffect(()=> {
    SetRespuesta(onzas())
  }, [dato,respuesta]);

  return (
    <div className="App">
      <label>Convertir libras a Onzas</label>
      <input type="text" value={dato}></input>
      <label>Onzas: {respuesta}</label>
    </div>
  );
}

export default App;
